package com.github.quartz.annotation;import java.lang.annotation.*;/** * Quartz任务定义注解 * 在缺省默认表配置时，可使用此种方式 */@Target({ElementType.METHOD})@Retention(RetentionPolicy.RUNTIME)@Documentedpublic @interface QuartzDefinition {    /**     * 默认当前方法类首字母小写名称     * @return     */    String targetBean() default "";    /**     * 默认当前方法名称     * @return     */    String targetMethod() default "";    /**     * 任务参数     *     * @return     */    String arguments() default "";    /**     * 默认当前方法名称     *  JobDetail     *  Trigger     * @return     */    String taskName() default "";    /**     * Quartz时间表达式     * @return     */    String cronExpression();}